import{_ as i,c as t,o as n,ag as r}from"./chunks/framework.dvv-DFtf.js";const h=JSON.parse('{"title":"ADR‑001: CR‑SQLite (Local‑First + CRDT)","description":"","frontmatter":{},"headers":[],"relativePath":"architecture/adr-001-crsqlite.md","filePath":"architecture/adr-001-crsqlite.md","lastUpdated":1768167711000}'),l={name:"architecture/adr-001-crsqlite.md"};function a(s,e,o,u,c,d){return n(),t("div",null,[...e[0]||(e[0]=[r('<h1 id="adr‐001-cr‐sqlite-local‐first-crdt" tabindex="-1">ADR‑001: CR‑SQLite (Local‑First + CRDT) <a class="header-anchor" href="#adr‐001-cr‐sqlite-local‐first-crdt" aria-label="Permalink to &quot;ADR‑001: CR‑SQLite (Local‑First + CRDT)&quot;">​</a></h1><ul><li><strong>Status:</strong> angenommen</li><li><strong>Datum:</strong> 2026‑01‑11</li></ul><h2 id="kontext" tabindex="-1">Kontext <a class="header-anchor" href="#kontext" aria-label="Permalink to &quot;Kontext&quot;">​</a></h2><p>FMB Log muss auf Netzlaufwerken funktionieren und mehrere Nutzer sollen parallel arbeiten können. Reines SQLite direkt auf SMB‑Shares ist dabei oft fehleranfällig (Dateisperren, Latenz, konkurrierende Writes). Ein klassischer DB‑Server (z. B. Postgres) ist in diesem Projekt bewusst nicht vorgesehen.</p><h2 id="entscheidung" tabindex="-1">Entscheidung <a class="header-anchor" href="#entscheidung" aria-label="Permalink to &quot;Entscheidung&quot;">​</a></h2><p>FMB Log nutzt <strong>CR‑SQLite</strong> (CRDT‑basierte Replikation als SQLite‑Extension), um ein <strong>Local‑First</strong> Modell umzusetzen:</p><ul><li>jeder Client schreibt in eine <strong>lokale Replica‑DB</strong> (AppLocalData),</li><li>ein optionaler <strong>Hub</strong> (Netzlaufwerk) dient als Austauschpunkt,</li><li>Clients synchronisieren Änderungen <strong>inkrementell</strong> (Pull/Push) über <code>crsql_changes</code>.</li></ul><p>Die Extension wird als Datei im Programm‑<code>resources/</code> gebündelt (Windows: <code>crsqlite.dll</code>) und vom SQL‑Layer geladen.</p><h2 id="alternativen-abgewogen" tabindex="-1">Alternativen (abgewogen) <a class="header-anchor" href="#alternativen-abgewogen" aria-label="Permalink to &quot;Alternativen (abgewogen)&quot;">​</a></h2><ol><li><strong>SQLite direkt auf Netzlaufwerk (Single‑File)</strong><ul><li><ul><li>sehr simpel</li></ul></li><li>− hohe Wahrscheinlichkeit für Locking-/SMB‑Probleme und Datenkorruption bei Fehlkonfiguration</li></ul></li><li><strong>DB‑Server (Postgres/MySQL)</strong><ul><li><ul><li>robuste Concurrency</li></ul></li><li>− Betrieb/Deployment/Firewall/Backups deutlich komplexer, widerspricht „kein Server“</li></ul></li><li><strong>Dokumenten‑DB ohne Server (z. B. LiteDB)</strong><ul><li><ul><li>keine SQL‑Migrations</li></ul></li><li>− Parallelzugriff/Locking auf Netzlaufwerken ebenfalls schwierig; weniger Integration in bestehende SQLite‑Logik</li></ul></li></ol><h2 id="konsequenzen" tabindex="-1">Konsequenzen <a class="header-anchor" href="#konsequenzen" aria-label="Permalink to &quot;Konsequenzen&quot;">​</a></h2><ul><li><strong>Schema‑Constraints (CRR‑Tauglichkeit):</strong><ul><li>Primary Keys müssen <strong>NOT NULL</strong> sein.</li><li><code>NOT NULL</code>‑Spalten benötigen <strong>DEFAULT</strong>‑Werte (Forward/Backward‑Compatibility).</li></ul></li><li><strong>IDs:</strong> keine Auto‑Increments als alleinige Schlüssel; stattdessen App‑seitig eindeutige IDs (z. B. <code>nanoid</code>).</li><li><strong>Migrations:</strong> Schemaänderungen erfolgen weiterhin über App‑Migrations; CR‑SQLite setzt dafür zusätzliche Regeln voraus.</li><li><strong>Fehlerklasse:</strong> Sync ist „eventual consistent“. Konflikte werden CRDT‑konform gelöst; dadurch sind bestimmte Datenmodelle (z. B. Zähler) leichter als harte Unique‑Constraints.</li><li><strong>Plattformabhängigkeit:</strong> Die CR‑SQLite Extension ist ein natives Binary pro Plattform.</li></ul><h2 id="hinweise-fur-betrieb-it" tabindex="-1">Hinweise für Betrieb/IT <a class="header-anchor" href="#hinweise-fur-betrieb-it" aria-label="Permalink to &quot;Hinweise für Betrieb/IT&quot;">​</a></h2><ul><li>Der Hub‑Ordner (Netzlaufwerk) muss für alle Clients <strong>lesbar/schreibbar</strong> sein.</li><li>Die lokale Replica‑DB muss pro Benutzer im AppLocalData Verzeichnis gespeichert werden (offline‑fähig).</li><li>Bei Diagnoseproblemen: zuerst prüfen, ob die Extension geladen wurde (<code>crsql_changes</code> verfügbar).</li></ul>',14)])])}const f=i(l,[["render",a]]);export{h as __pageData,f as default};
