import{_ as n,c as t,o as i,ag as r}from"./chunks/framework.B6P7veLL.js";const c=JSON.parse('{"title":"ADR‑002: Integrität & Signaturen (Ed25519 + Delegation)","description":"","frontmatter":{},"headers":[],"relativePath":"architecture/adr-002-integrity-signing.md","filePath":"architecture/adr-002-integrity-signing.md","lastUpdated":1768213913000}'),a={name:"architecture/adr-002-integrity-signing.md"};function l(s,e,o,g,u,d){return i(),t("div",null,[...e[0]||(e[0]=[r('<h1 id="adr‐002-integritat-signaturen-ed25519-delegation" tabindex="-1">ADR‑002: Integrität &amp; Signaturen (Ed25519 + Delegation) <a class="header-anchor" href="#adr‐002-integritat-signaturen-ed25519-delegation" aria-label="Permalink to &quot;ADR‑002: Integrität &amp; Signaturen (Ed25519 + Delegation)&quot;">​</a></h1><ul><li><strong>Status:</strong> angenommen</li><li><strong>Datum:</strong> 2026‑01‑11</li></ul><h2 id="kontext" tabindex="-1">Kontext <a class="header-anchor" href="#kontext" aria-label="Permalink to &quot;Kontext&quot;">​</a></h2><p>Wenn die Hub‑DB auf einem Netzlaufwerk liegt, können Nutzer (oder Malware im Benutzerkontext) Dateien direkt verändern. Ohne zusätzliche Maßnahmen wären Manipulationen an Stammdaten oder Messdaten ggf. nicht erkennbar (z. B. Rechte eskalieren, Freigabewerte ändern, Messungen nachträglich verändern).</p><p>Gleichzeitig sollen bestimmte nicht‑Admin Rollen (Key‑User) <strong>fachliche Stammdaten</strong> pflegen dürfen, ohne Zugriff auf Admin‑Signier‑Passwörter zu erhalten.</p><h2 id="entscheidung" tabindex="-1">Entscheidung <a class="header-anchor" href="#entscheidung" aria-label="Permalink to &quot;Entscheidung&quot;">​</a></h2><p>FMB Log implementiert einen mehrstufigen Integritätsschutz:</p><ol><li><strong>Root‑Trust (Compile‑Time)</strong><ul><li>Der <strong>Root Public Key</strong> ist fest in der Anwendung enthalten.</li><li>Damit kann die App bestimmte Zertifikate verifizieren, ohne auf veränderliche Dateien zu vertrauen.</li></ul></li><li><strong>DB‑Key‑Zertifikat (Root‑signiert)</strong><ul><li>Eine Datei <code>&lt;db&gt;.integrity.dbkey.json</code> enthält den <strong>DB‑Public‑Key</strong> und eine <strong>Root‑Signatur</strong> darüber.</li><li>Damit kann der DB‑Key wiederhergestellt und verifiziert werden (auch wenn <code>&lt;db&gt;.integrity.pub.json</code> fehlt).</li></ul></li><li><strong>DB‑Signierschlüssel in Stronghold</strong><ul><li>Der <strong>DB‑Private Key</strong> liegt verschlüsselt im Stronghold‑Vault (<code>vaults/&lt;db&gt;.integrity.vault</code>).</li><li>Admins entsperren den Vault (Signier‑Passwort) für Signier‑Operationen.</li></ul></li><li><strong>Per‑User‑Signaturen + Delegation</strong><ul><li>Nutzer besitzen zusätzlich einen <strong>User‑Signierschlüssel</strong> (ebenfalls im Stronghold‑Vault <code>vaults/&lt;user_id&gt;.vault</code>).</li><li>Ein Admin kann per <strong>Capability‑Zertifikat</strong> delegieren, dass ein Nutzer innerhalb eines Scopes (z. B. <code>masterdata.fgw</code>) signieren darf.</li><li>So können Key‑User fachliche Daten pflegen, ohne Admin‑Signier‑Passwort.</li></ul></li><li><strong>Audit</strong><ul><li>Ein Audit prüft (und cached) die Integrität der signierten Bereiche und macht Abweichungen sichtbar.</li></ul></li></ol><h2 id="hashing" tabindex="-1">Hashing <a class="header-anchor" href="#hashing" aria-label="Permalink to &quot;Hashing&quot;">​</a></h2><ul><li>Für interne Integritäts‑Fingerprints und Snapshot‑Hashes wird <strong>BLAKE3</strong> verwendet (schnell, parallelisierbar).</li><li>Für RFC‑3161 TSA‑Requests wird weiterhin <strong>SHA‑256</strong> genutzt (TSA‑Standard).</li></ul><h2 id="alternativen-abgewogen" tabindex="-1">Alternativen (abgewogen) <a class="header-anchor" href="#alternativen-abgewogen" aria-label="Permalink to &quot;Alternativen (abgewogen)&quot;">​</a></h2><ol><li><strong>Nur ACLs / Dateirechte</strong><ul><li><ul><li>simpel</li></ul></li><li>− schützt nicht vor Manipulationen im gültigen Benutzerkontext</li></ul></li><li><strong>DB‑Verschlüsselung</strong><ul><li><ul><li>erschwert direkten Zugriff</li></ul></li><li>− Key‑Management schwierig; kann Concurrency/CRDT‑Setup verschärfen</li></ul></li><li><strong>Signaturen nur für PDFs</strong><ul><li><ul><li>Endprodukt geschützt</li></ul></li><li>− Stammdaten/Messdaten in der DB wären weiterhin manipulierbar und würden erst spät auffallen</li></ul></li></ol><h2 id="konsequenzen" tabindex="-1">Konsequenzen <a class="header-anchor" href="#konsequenzen" aria-label="Permalink to &quot;Konsequenzen&quot;">​</a></h2><ul><li><strong>Betriebsprozess:</strong> Das DB‑Key‑Zertifikat ist verpflichtend (Backup/Recovery). Die Datei muss separat gesichert werden.</li><li><strong>Rollenmodell:</strong> Admin‑Entsperren bleibt seltene Aktion; fachliche Signaturen können per Delegation erfolgen.</li><li><strong>Fail‑Closed:</strong> Wenn Integrität aktiviert ist, werden nicht verifizierbare Daten als kritisch behandelt (z. B. nicht freigabefähig / Blocken bestimmter Admin‑Operationen).</li></ul><h2 id="offene-punkte-spatere-ausbaustufen" tabindex="-1">Offene Punkte / spätere Ausbaustufen <a class="header-anchor" href="#offene-punkte-spatere-ausbaustufen" aria-label="Permalink to &quot;Offene Punkte / spätere Ausbaustufen&quot;">​</a></h2><ul><li>Optionale RFC‑3161 Zeitstempelung für Tagesabrechnungen (erfordert Internet; TSA‑Token in Historie/QR).</li><li>Erweiterte Integrität über Protokoll‑Packfiles (Packfile‑Hash/Index signieren).</li></ul>',16)])])}const f=n(a,[["render",l]]);export{c as __pageData,f as default};
